public int[] shortestAlternatingPaths(int n, int[][] redEdges, int[][] blueEdges)
{
    List<Integer>[] redList = new List[n];
    List<Integer>[] blueList = new List[n];

    for(int i = 0; i < n; ++i)
    {
        redList[i] = new ArrayList<>();
        blueList[i] = new ArrayList<>();
    }

    for(int[] e : redEdges)
    {
        redList[e[0]].add(e[1]);
    }

    for(int[] e : blueEdges)
    {
        blueList[e[0]].add(e[1]);
    }

    int[] redAns = new int[n];//  [] i [] 
    int[] blueAns = new int[n];//  [] i [] 

    for(int i = 1; i < n; i++)//   []  [MAX]
    {                         //  [1]  [0]  [0]  [0] 
        redAns[i] = Integer.MAX_VALUE;
        blueAns[i] = Integer.MAX_VALUE;
    }

    Queue<int[]> queue = new ArrayDeque<>();//  [2] 
                                            //  +  []

    queue.add(new int[]{0, 0});// [0]  []
    queue.add(new int[]{0, 1});// [1]  []
                               //  [2]  []  []

    int level = 0;// bfs = 
                  //  [i]   [0]  [i] 

    while(!queue.isEmpty())
    {
        level++;// bfs [+1] 

        int size = queue.size();

        for(int i = 0; i < size; i++)// 
        {
            int[] curArr = queue.poll();
            int cur = curArr[0];

            if(curArr[1] == 0)//  []
            {
                for(int next : redList[cur])//  [] 'next'
                {
                    if(level < redAns[next])//  [] 'next'
                    {                       //  []  [] 

                        redAns[next] = level;//  [] 
                        queue.offer(new int[]{next, 1});//'next'  []
                    }
                }
            }
            else//  [] 
